  <button class="hero__page-down" on:click={scrollIntoView}>
    <img src={downIcon} alt="To projects" />
  </button>
</section>

<style>
  .hero {
    background-color: var(--background-clr-hero);
    height: calc(100vh + 1px);
    padding-top: 14rem;
    max-width: 768px;
    text-align: center;
  }

  .hero__name-and-title svg {
    width: 95%;
  }

  .hero__name-and-title__title {
    color: var(--background-clr-card);
    font-size: 0.8rem;
    font-weight: 600;
    opacity: 0;
    animation: opacityTranslateY 1s ease-in-out 1 forwards;
    animation-delay: 1s;
    margin-bottom: 4rem;
  }

  .hero__about {
    color: var(--text-clr-tertiary);
  }

  .hero__about__text {
    line-height: 1.8;
    margin: auto;
    max-width: 90%;
  }

  .hero__about__word {
    display: inline-block;
    opacity: 0;
    animation: wordOpacityTranslateY 3s ease-in-out forwards;
    animation-delay: calc(0.05s * var(--i));
  }

  .hero__about__seperator {
    background-color: var(--text-clr-primary);
    opacity: 0;
    height: 1px;
    width: 10rem;
    animation: seperator 1.5s ease-in-out forwards;
    animation-delay: 3.5s;
    margin: 2rem auto 3rem;
  }

  .hero__socials {
    align-self: start;
    animation: opacity 2s;
  }

  .hero__socials > a {
    margin: 0 0.25rem;
  }

  .hero__socials__icon {
    height: 2.5rem;
    width: 2.5rem;
    filter: grayscale(100%) brightness(200%);
  }

  .hero__socials__icon:hover {
    transform: scale(1.1);
    transition: 0.2s ease-out;
    filter: grayscale(0) brightness(100%);
  }

  .hero__socials__icon:active {
    transform: scale(0.9);
    transition: 0.01s;
  }

  svg path {
    fill: transparent;
    stroke: var(--text-clr-primary);
    stroke-width: 1;
    stroke-dasharray: 300;
    stroke-dashoffset: 300;
    animation: textFill 3s ease-in-out 1 forwards;
  }

  .hero__page-down {
    position: absolute;
    left: 50%;
    bottom: 0.5rem;
    transform: translate(-50%, -50%);
  }
  .hero__page-down > img {
    height: 2rem;
    width: 2rem;
    animation: grow 2s ease-in-out infinite;
  }

  .hero__page-down > img:hover {
    cursor: pointer;
  }

  @media (min-width: 768px) {
    .hero {
      display: flex;
      align-items: center;
      margin: auto;
      padding-top: 0;
    }

    .hero__name-and-title svg {
      width: 650px;
    }

    .hero__name-and-title__title {
      font-size: 1rem;
    }
  }

  @keyframes textFill {
    0% {
      stroke-dashoffset: 300;
    }
    55% {
      fill: transparent;
    }
    100% {
      fill: var(--text-clr-primary);
      stroke-dashoffset: 0;
    }
  }

  @keyframes opacity {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }

  @keyframes opacityTranslateY {
    0% {
      opacity: 0;
      transform: translateY(5px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes wordOpacityTranslateY {
    0% {
      opacity: 0;
    }
    60% {
      opacity: 0;
      transform: translateY(30px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes seperator {
    0% {
      opacity: 1;
      transform: scale(0, 1);
    }
    100% {
      opacity: 1;
      transform: scale(1, 1);
    }
  }

  @keyframes grow {
    0% {
      transform: scale(1);
    }
    40% {
      transform: scale(1.3);
    }
    100% {
      transform: scale(1);
    }
  }

  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }
</style>
